import React from "react";

class GoogleAuth extends React.Component {
  state = { isSignedIn: null };
  componentDidMount() {
    window.gapi.load("client:auth2", () => {
      window.gapi.client
        .init({
          clientId:
            "856335963131-ushhuq78dsj7lnak8ocdupqdo0h8oo2j.apps.googleusercontent.com",
          scope: "email"
        })
        .then(() => {
          this.auth = window.gapi.auth2.getAuthInstance();
          this.setState({ isSignedIn: this.auth.isSignedIn.get() });
          this.auth.isSignedIn.listen(onAuthChange());
        });
    });
  }
  onAuthChange() {}
  renderAuthButton() {
    if (this.state.isSignedIn === null) {
      return <div>I don't know if you are sign in</div>;
    } else if (this.state.isSignedIn === true) {
      return <div>You are sign in!</div>;
    } else {
      return <div>You are NOT sing in!</div>;
    }
  }
  render() {
    return <div>{this.renderAuthButton()}</div>;
  }
}

export default GoogleAuth;
