import React from "react";
import { connect } from "react-redux";
import { fetchStreams } from "../../actions";

class StreamList extends React.Component {
  componentDidMount() {
    this.props.fetchStreams();
  }

  renderAdmin(stream) {
    if (this.props.currendUserId === stream.userId) {
      return <div>EDIT/DELETE</div>;
    }
  }
  renderStreamList() {
    return this.props.streams.map(({ title, description, id }) => {
      return (
        <div className="item" key={id}>
          <i className="large middle aligned icon camera" />
          <div className="content">
            <h4>{title}</h4>
            <div className="description">{description}</div>
          </div>
        </div>
      );
    });
  }

  render() {
    this.renderStreamList();
    return (
      <div>
        <h2>Streams</h2>
        <div className="ui celled list">{this.renderStreamList()}</div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    streams: Object.values(state.streams),
    currendUserId: state.auth.userId
  };
};
export default connect(
  mapStateToProps,
  { fetchStreams }
)(StreamList);
